import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from "@angular/forms";
import { Location } from '@angular/common';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-filtro-peliculas',
  templateUrl: './filtro-peliculas.component.html',
  styleUrls: ['./filtro-peliculas.component.scss']
})
export class FiltroPeliculasComponent implements OnInit {

  constructor(private formBuilder: FormBuilder,
    private location: Location,
    private activatedRoute: ActivatedRoute) { }

  formGroup: FormGroup | any;

  generosPeliculas = [
    { id: 1, nombre: 'Drama' },
    { id: 2, nombre: 'Acci√≥n' },
    { id: 3, nombre: 'Comedia' },
    { id: 4, nombre: 'Terror' },
  ];

  listadoPeliculas = [
    {titulo: 'Spider-Man No Way Home', inCines: false, proximosEstrenos: true, generos:[1,2], poster:'data:image/jpeg;base64,'},
    {titulo: 'Moana', inCines: false, proximosEstrenos: true, generos:[2,3], poster:'data:image/jpeg;base64,'},
    {titulo: 'Hereditary', inCines: false, proximosEstrenos: false, generos:[1,4], poster:'data:image/jpeg;base64,'},
  ]

  listadoPeliculasOriginal = this.listadoPeliculas;

  formularioOriginal = {
    titulo: '',
    generoId: 0,
    proximosEstrenos: false,
    inCines: false
  }

  ngOnInit(): void {
    this.formGroup = this.formBuilder.group(this.formularioOriginal);
    this.leerValoresUrl();
    this.buscarPeliculas(this.formGroup.value);

    this.formGroup.valueChanges
    .subscribe((valores: any) => {
      this.listadoPeliculas = this.listadoPeliculasOriginal;
      this.buscarPeliculas(valores);
      this.escribirParametrosBusquedaEnUrl();
    })   
    
  }

  private leerValoresUrl(){
    this.activatedRoute.queryParams.subscribe((params) => {
      var objeto: any = {};

      if(params['titulo']){
        objeto.titulo =params['titulo'];
      }

      if(params['generoId']){
        objeto.generoId = Number(params['generoId']);
      }

      if(params['proximosEstrenos']){
        objeto.proximosEstrenos = params['proximosEstrenos'];
      }

      if(params['inCines']){
        objeto.inCines = params['inCines'];
      }

      this.formGroup.patchValue(objeto);
    })
  }

  private escribirParametrosBusquedaEnUrl(){
    var queryStrings = [];
    var valoresFormulario = this.formGroup.value;

    if(valoresFormulario.titulo){
      queryStrings.push(`titulo=${valoresFormulario.titulo}`)
    }

    if(valoresFormulario.generoId != '0'){
      queryStrings.push(`generoId=${valoresFormulario.generoId}`);
    }

    if(valoresFormulario.proximosEstrenos){
      queryStrings.push(`proximosEstrenos=${valoresFormulario.proximosEstrenos}`);
    }

    if(valoresFormulario.inCines){
      queryStrings.push(`inCines=${valoresFormulario.inCines}`);
    }

    this.location.replaceState('peliculas/buscar', queryStrings.join('&'));

  }

  buscarPeliculas(valores: any){

    if(valores.titulo){
      this.listadoPeliculas = this.listadoPeliculas.filter(listadoPelicula => listadoPelicula.titulo.indexOf(valores.titulo) !== -1);
    }    

    if(valores.generoId !== 0){
      this.listadoPeliculas = this.listadoPeliculas.filter(listadoPelicula => listadoPelicula.generos.indexOf(valores.generoId) !== -1);
    }

    if(valores.proximosEstrenos){
      this.listadoPeliculas = this.listadoPeliculas.filter(listadoPelicula => listadoPelicula.proximosEstrenos);
    }

    if(valores.inCines){
      this.listadoPeliculas = this.listadoPeliculas.filter(listadoPelicula => listadoPelicula.inCines);
    }

  }

  limpiar(){
    this.formGroup.patchValue(this.formularioOriginal);
  }

}





